var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	
	function TestCase_GlideRecordQuery(vInput, bGhost, vExpected, strExpectedSysId)
	{
		Log('TestCase_GlideRecordQuery');
		Log('Input          : '+RenderValue(vInput));
		Log('Ghost          : '+RenderValue(bGhost));
		Log('Expected       : '+RenderValue(vExpected));
		Log('Expected sys_id: '+RenderValue(strExpectedSysId))
		var vResult = GlideRecordQuery(vInput,bGhost);
		Log('Result         : '+RenderValue(vResult));
		if (vResult == true)
		{
			Log('Row count      : '+vInput.getRowCount());
		}
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		if (bResult == true)
		{
			bResult = false;
			while (vInput.next())
			{
				if (GetStringValue(vInput.sys_id) == strExpectedSysId)
				{
					bResult = true;
				}
			}
			if (bResult == false)
			{
				Log('The query result did not contain the expected sys_id.');
				Log('Test case FAILED');
			}
		}
		Log('\n\n');
	}

	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = '_ATF_DEVTOOLS_TEST1';
	grTest.value = 'Hello World';
	var strTestSysId = grTest.insert();

	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.addQuery('name','=','_ATF_DEVTOOLS_TEST1');
	TestCase_GlideRecordQuery(grTest,false,true,strTestSysId);
	Log(RenderValue(grTest)+'\n\n');

	
	var grAppModule = new GlideRecord('sys_app_module');
	grAppModule.addQuery('title','=','Test Suite');
	grAppModule.addQuery('application','=','60e9f6d01bd38450a89720a8ec4bcb86'); // DevTools menu
	grAppModule.addQuery('sys_scope','=','d689f6901bd38450a89720a8ec4bcbfd'); // DevTools app 
	TestCase_GlideRecordQuery(grAppModule,false,true,'38265309dbfb4810ba15a35305961983'); // "Test Suite" module
	//Log(RenderValue(grAppModule));
	
	var grSI = new GlideRecord('sys_script_include');
	grSI.addQuery('sys_id','=','202de37adbdcfc103c6c09c2ba9619da'); // "GlideRecordQuery" script include
	TestCase_GlideRecordQuery(grSI,false,true,'202de37adbdcfc103c6c09c2ba9619da'); // "GlideRecordQuery" script include
	//Log(RenderValue(grSI));

	var grSI = new GlideRecord('sys_script_include');
