sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	

	var strSysIdUser1 = testdata.CreateUser('USER1');
	var strSysIdUser2 = testdata.CreateUser('USER2');

	var strSysIdCompany1 = testdata.CreateCompany('COMPANY1',{ city: 'Frankfurt', country: 'DEU'});
	var strSysIdCompany2 = testdata.CreateCompany('COMPANY2',{ city: 'New York', country: 'USA'});

	var strSysIdContract1 = testdata.CreateContract('CONTRACT1',{ });
	var strSysIdContract2 = testdata.CreateContract('CONTRACT2',{ });
	
	
	test.TestCase_GetRecord('sys_user',strSysIdUser1,false,true);
	test.TestCase_GetRecord('sys_user',strSysIdUser2,false,true);
	test.TestCase_GetRecord('sys_user',testdata.GetUser('USER1'),false,true);
	test.TestCase_GetRecord('sys_user',testdata.GetUser('USER2'),false,true);
	
	
	test.TestCase_GetRecord('core_company',strSysIdCompany1,false,true);
	test.TestCase_GetRecord('core_company',strSysIdCompany2,false,true);
	test.TestCase_GetRecord('core_company',testdata.GetCompany('COMPANY1'),false,true);
	test.TestCase_GetRecord('core_company',testdata.GetCompany('COMPANY2'),false,true);
	
	test.TestCase_GetRecord('ast_contract',strSysIdContract1,false,true);
	test.TestCase_GetRecord('ast_contract',strSysIdContract2,false,true);
	test.TestCase_GetRecord('ast_contract',testdata.GetContract('CONTRACT1'),false,true);
	test.TestCase_GetRecord('ast_contract',testdata.GetContract('CONTRACT2'),false,true);
	
	
	
	Log(RenderValue(testdata.GetAllData()));

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
