sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_CreatePDFFromHtml(
		strTable,
		strRecordSysId,
		strFileName,
		strHtml,
		strPageSize,
		strOrientation,
		fMarginTopBottom,
		fMarginLeftRight,
		bPageNumber,
		vExpected)
	{
		function TestCase_CreatePDFFromHtml_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == vResult)
			{
				return true;
			}
			if (vExpected == true)
			{
				return IsValidSysId(vResult);
			}
			return false;
		}
		test.TestCase(
			'Function CreatePDFFromHtml()',
			CreatePDFFromHtml,
			{
				'Table': strTable,
				'Record': strRecordSysId,
				'Filename': strFileName,
				'Html': strHtml,
				'Page Size': strPageSize,
				'Orientation': strOrientation,
				'Margin top/bottom': fMarginTopBottom,
				'Margin left/right': fMarginLeftRight,
				'Page Numbering': bPageNumber
			},
			vExpected,
			TestCase_CreatePDFFromHtml_CheckResult);
	}


	TestCase_CreatePDFFromHtml(null,null,'test.pdf','<p>This is a test!</p>','A4','PORTRAIT',1,1,false,false);
	
	var testdata = new TestDataAPI('_DEVTOOLS_');
	var strRecordSysId = testdata.CreateTestItem('ATTACHMENT1');
	
	TestCase_CreatePDFFromHtml('x_snc_devtools_test',strRecordSysId,'test.pdf','<p>This is a test!</p>','A4','PORTRAIT',1,1,false,true);
	
	var gsa = new GlideSysAttachment();
	var grAttachments = gsa.getAttachments('x_snc_devtools_test', strRecordSysId);

	while (grAttachments.next())
	{
		test.Log(RenderRecordSummary(grAttachments));
	}
	if (grAttachments.getRowCount() != 1)
	{
		test.Log('FAILED: Record should have exactly one attachment!');
		test.SetResult(false);
	}
	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
