sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RecordTransporterRecordUnpack(vPackage, vTransportRecord, vExpected)
	{
		function TestCase_RecordTransporterRecordUnpack_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function RecordTransporterRecordUnpack()',
			RecordTransporterRecordUnpack,
			{
				'Package': vPackage,
				'Transport Record': vTransportRecord,
			},
			vExpected,
			TestCase_RecordTransporterRecordUnpack_CheckResult);
	}

	TestCase_RecordTransporterRecordUnpack(null,null,false);

	var grPackage = new GlideRecord('x_snc_devtools_transporter_package');
	grPackage.initialize();
	grPackage.name = '_ATF_DEVTOOLS_Test1';
	grPackage.id_suffix = 'atf_test1';
	grApp = GetAppRecord('x_snc_devtools');
	grPackage.sys_scope = grApp.sys_id;
	var strPackageSysId = grPackage.insert();
	test.Log(grPackage);

	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Test1';
	grTest.value = 'Hello World';
	var strTestSysId = grTest.insert();
	test.Log(grTest);

	var strTransportRecordSysId = RecordTransporterRecordPack(grPackage,grTest);

	RecordDelete('x_snc_devtools_test',strTestSysId);

	TestCase_RecordTransporterRecordUnpack(grPackage,strTransportRecordSysId,true);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
