sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetInstanceListPath(strTable, strView, strEncodedQuery, nFirstRow, vExpected)
	{
		test.TestCase(
			'GetInstanceListPath()',
			GetInstanceListPath,
			{
				'Table': strTable,
				'View': strView,
				'Encoded Query': strEncodedQuery,
				'First Row': nFirstRow
			},
			vExpected,
			null);
	}


	TestCase_GetInstanceListPath(null,null,null,null,
		false);
	TestCase_GetInstanceListPath('',null,null,null,
		false);
	
	TestCase_GetInstanceListPath('incident',null,null,0,
		'nav_to.do?uri=%2Fincident_list.do%3F');
	
	TestCase_GetInstanceListPath('incident','DevTools',null,0,
		'nav_to.do?uri=%2Fincident_list.do%3F%26sysparm_view%3DDevTools%26sysparm_view_forced%3Dtrue');
	
	TestCase_GetInstanceListPath('incident',null,'active=true',0,
		'nav_to.do?uri=%2Fincident_list.do%3F%26sysparm_query%3Dactive%253Dtrue');
	
	TestCase_GetInstanceListPath('incident','DevTools','active=true',0,
		'nav_to.do?uri=%2Fincident_list.do%3F%26sysparm_view%3DDevTools%26sysparm_view_forced%3Dtrue%26sysparm_query%3Dactive%253Dtrue');

	TestCase_GetInstanceListPath('incident','DevTools','active=true',100,
		'nav_to.do?uri=%2Fincident_list.do%3F%26sysparm_view%3DDevTools%26sysparm_view_forced%3Dtrue%26sysparm_query%3Dactive%253Dtrue%26sysparm_first_row%3D100');


	
	//%26sysparm_first_row%3D1
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
