sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_InstanceScanPointScan(strTable, strSysId, vExpected)
	{
		function TestCase_InstanceScanPointScan_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vResult == false);
			}
			return (vResult !== false);
		}	
		
		test.TestCase(
			'Function InstanceScanPointScan()',
			InstanceScanPointScan,
			{
				'Table': strTable,
				'SysId':  strSysId,
				'Detailed': false
			},
			vExpected,
			TestCase_InstanceScanPointScan_CheckResult);
	}


	TestCase_InstanceScanPointScan('sys_script_include','',false);
	TestCase_InstanceScanPointScan('sys_script_include','12345678901234567890123456789012',false);

	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');

	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test1',
		'function _ATF_DEVTOOLS_Test1()\n'
		+'{ return true; }');
	TestCase_InstanceScanPointScan('sys_script_include',strRecordSysId,true);
	
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test2',
		'var globalvalue = 0;\n');
	TestCase_InstanceScanPointScan('sys_script_include',strRecordSysId,true);
	
	test.Log(RenderValue(testdata.GetAllData()));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
