sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ParseUrl_CheckResult(vInput,vExpected,vResult)
	{
		return IsEqual(vExpected,vResult);
	}
	
	function TestCase_ParseUrl(strUrl, vExpected)
	{
		test.TestCase(
			'Function ParseUrl()',
			ParseUrl,
			{
				'Url': strUrl 
			},
			vExpected,
			TestCase_ParseUrl_CheckResult);
	}


	TestCase_ParseUrl(null,false);
	TestCase_ParseUrl('',false);
	TestCase_ParseUrl(' ',false);

	TestCase_ParseUrl('http://www.example.com',
		{
			protocol: 'http',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: '',
			parameters: '',
			anchor: ''
		});
	

	TestCase_ParseUrl('https://www.example.com:8080',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '8080',
			user: '',
			password: '',
			path: '',
			parameters: '',
			anchor: ''
		});

	TestCase_ParseUrl('https://www.example.com/path1/',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: 'path1/',
			parameters: '',
			anchor: ''
		});

	TestCase_ParseUrl('https://www.example.com/?key=value',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: '',
			parameters: 'key=value',
			anchor: ''
		});

	TestCase_ParseUrl('https://www.example.com/#anchor1',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: '',
			parameters: '',
			anchor: 'anchor1'
		});
	
	
	TestCase_ParseUrl('https://www.example.com/path/',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: 'path/',
			parameters: '',
			anchor: ''
		});
	
	TestCase_ParseUrl('https://www.example.com/path/?key=value',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: 'path/',
			parameters: 'key=value',
			anchor: ''
		});
	
	TestCase_ParseUrl('https://www.example.com/path/?key1=value1&key2=value2',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: 'path/',
			parameters: 'key1=value1&key2=value2',
			anchor: ''
		});	
	
	TestCase_ParseUrl('https://www.example.com/path/?key1=value1&key2=value2#anchor1',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '',
			user: '',
			password: '',
			path: 'path/',
			parameters: 'key1=value1&key2=value2',
			anchor: 'anchor1'
		});		

	TestCase_ParseUrl('https://www.example.com:443/path/?key1=value1&key2=value2#anchor1',
		{
			protocol: 'https',
			host: 'www.example.com',
			port: '443',
			user: '',
			password: '',
			path: 'path/',
			parameters: 'key1=value1&key2=value2',
			anchor: 'anchor1'
		});		
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
