sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RecordTransporterPackagePackTranslatedText(vPackage, query, vExpected)
	{
		function TestCase_RecordTransporterPackagePackTranslatedText_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function RecordTransporterPackagePackTranslatedText()',
			RecordTransporterPackagePackTranslatedText,
			{
				'Package': vPackage,
				'Query': query
			},
			vExpected,
			TestCase_RecordTransporterPackagePackTranslatedText_CheckResult);
	}

	TestCase_RecordTransporterPackagePackTranslatedText(null,null,false);


	var grPackage = new GlideRecord('x_snc_devtools_transporter_package');	
	grPackage.initialize();
	grPackage.name = '_ATF_DEVTOOLS_Test1';
	grPackage.id_suffix = 'atf_test1';
	grApp = GetAppRecord('x_snc_devtools');
	grPackage.sys_scope = grApp.sys_id;
	var strPackageSysId = grPackage.insert();
	test.Log(grPackage);

	var grTranslatedText = new GlideRecord('sys_translated_text');
	grTranslatedText.initialize();
	grTranslatedText.tablename = 'sys_app';
	grTranslatedText.documentkey = grApp.sys_id;
	grTranslatedText.fieldname = 'short_description';
	grTranslatedText.language = 'de';
	grTranslatedText.value = 'Eine kurze Beschreibung der DevTools auf deutsch...';
	var strTranslatedTextSysId = GlideRecordInsert(grTranslatedText);
	test.Log(grTranslatedText);

	var grTranslatedText = new GlideRecord('sys_translated_text');
	grTranslatedText.initialize();
	grTranslatedText.tablename = 'sys_app';
	grTranslatedText.documentkey = grApp.sys_id;
	grTranslatedText.fieldname = 'short_description';
	grTranslatedText.language = 'fr';
	grTranslatedText.value = 'Et en francaise...';
	var strTranslatedTextSysId = GlideRecordInsert(grTranslatedText);
	test.Log(grTranslatedText);

	var query = { documentkey: grApp.sys_id, language: 'de' };
	TestCase_RecordTransporterPackagePackTranslatedText(grPackage,query,true);

	var grTransportRecord = new GlideRecord('x_snc_devtools_transporter_record');
	grTransportRecord.addQuery('package','=',grPackage.sys_id);
	grTransportRecord.query();
	if (grTransportRecord.getRowCount() != 1)
	{
		test.Log('FAILED: Unexpected amount of transport records.');
		test.SetResult(false);
	}
	while (grTransportRecord.next())
	{
		test.Log(grTransportRecord);
	}
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
