sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_Merge(vValue1, vValue2, vExpected)
	{
		function TestCase_Merge_CheckResult(vInput,vExpected,vResult)
		{
			return IsEqual(vExpected,vResult);
		}
		
		test.TestCase(
			'Function Merge()',
			Merge,
			{
				'Value1': vValue1,
				'Value2': vValue2
			},
			vExpected,
			TestCase_Merge_CheckResult);
	}

	TestCase_Merge(null,null,false);
	TestCase_Merge([],{},false);
	TestCase_Merge({},[],false);
		
	
	TestCase_Merge({},{},{});
	TestCase_Merge(null,{},{});
	TestCase_Merge({},null,{});
	TestCase_Merge(
		{
			firstname:'John'
		},
		{
			lastname:'Doe'
		},
		{
			firstname:'John',
			lastname:'Doe'
		});
	TestCase_Merge(
		{
			first_name:'John',
			last_name:'Doe'
		},
		{
			email: 'john.doe@example.com'
		},
		{
			first_name:'John',
			last_name:'Doe',
			email: 'john.doe@example.com'
		});	
	
	TestCase_Merge(
		{
			firstname:'John'
		},
		{
			firstname:'Jane',
			lastname:'Doe'
		},
		{
			firstname:'Jane',
			lastname:'Doe'
		});
	TestCase_Merge(
		{
			firstname:'John',
			kids:
			{
				first: 'Alice'
			}
		},
		{
			firstname:'Jane',
			lastname:'Doe',
			kids:
			{
				second: 'Bobby'
			}
		},
		{
			firstname:'Jane',
			lastname:'Doe',
			kids:
			{
				first: 'Alice',
				second: 'Bobby'
			}
		});
	
	
	TestCase_Merge(
		{
		},
		{
			level1:
			{
				level2:
				{
					value: 'Alpha'
				}
			}
		},
		{
			level1:
			{
				level2:
				{
					value: 'Alpha'
				}
			}
		});
	
	TestCase_Merge([],[],[]);
	TestCase_Merge(null,[],[]);
	TestCase_Merge([],null,[]);
		
	TestCase_Merge(['Alpha'],['Beta'],['Alpha','Beta']);
	TestCase_Merge(['Alpha','Beta'],['Beta'],['Alpha','Beta']);
	TestCase_Merge(['Alpha'],[],['Alpha']);

	TestCase_Merge([ {Hello:'World'} ],[ {Hello:'Universe'} ],[ {Hello:'World'},{Hello:'Universe'} ]);

	TestCase_Merge({ Array1: ['Alpha'] }, { Array1: ['Beta'] }, { Array1: ['Alpha','Beta'] });
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
