sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);


	function TestCase_GetPackageDependencyTree(vPackage, vExpected)
	{
		function TestCase_GetPackageDependencyTree_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			var aDependencies = GetArrayValue(GetValue(vResult,'_dependencies'));
			if (GetValue(vExpected,'count') != aDependencies.length)
			{
				test.Log('Unexpected dependency count.');
				return false;
			}
			if (GetValue(vExpected,'dependency_status_summary') != GetValue(vResult,'dependency_status_summary'))
			{
				test.Log('Unexpected dependency status summary.');
				return false;
			}	
			return true;
		}

		test.TestCase(
			'Function GetPackageDependencyTree()',
			GetPackageDependencyTree,
			{
				'Package': vPackage
			},
			vExpected,
			TestCase_GetPackageDependencyTree_CheckResult);
	}

	TestCase_GetPackageDependencyTree(null,false);
	TestCase_GetPackageDependencyTree('',false);

	TestCase_GetPackageDependencyTree('x_snc_devtools',{ count: 0, dependency_status_summary: 'ok' });
	
	
	var grApp = GetAppRecord('x_snc_devtools');

	var grPackagePackage = new GlideRecord('sys_plugins');
	grPackagePackage.addQuery('name','=','Package');
	grPackagePackage.query();
	grPackagePackage.next();

	var grPackageServicePortal = new GlideRecord('sys_plugins');
	grPackageServicePortal.addQuery('name','=','Service Portal - Core');
	grPackageServicePortal.query();
	grPackageServicePortal.next();
	
	
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grApp.sys_id;
	grDependency.dependency = grPackagePackage.sys_id;
	grDependency.name = 'Package';
	grDependency.insert();
	TestCase_GetPackageDependencyTree('x_snc_devtools',{ count: 1, dependency_status_summary: 'ok' });		

	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grApp.sys_id;
	grDependency.dependency = grPackageServicePortal.sys_id;
	grDependency.name = 'Service Portal - Core';
	grDependency.versions = '1000.0.0';
	grDependency.insert();
	TestCase_GetPackageDependencyTree('x_snc_devtools',{ count: 2, dependency_status_summary: 'incompatible_version' });
	
	
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grApp.sys_id;
	grDependency.dependency = GetPseudoSysId();
	grDependency.name = 'Pseudo';
	grDependency.insert();
	TestCase_GetPackageDependencyTree('x_snc_devtools',{ count: 3, dependency_status_summary: 'missing' });

	// "Service Portal - Core" now has a dependency to "DevTools" - this would be a circular dependency which is not allowed!  
	var grDependency = new GlideRecord('x_snc_devtools_dependency');
	grDependency.initialize();
	grDependency.sys_scope = grPackageServicePortal.sys_id;
	grDependency.dependency = grApp.sys_id;
	grDependency.name = 'DevTools';
	grDependency.insert();
	TestCase_GetPackageDependencyTree('x_snc_devtools',{ count: 3, dependency_status_summary: 'circular' });
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
