sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RecordQuery(strTable, query, vExpected)
	{
		function TestCase_RecordQuery_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected === false)
			{
				return (vExpected == vResult);
			}
			else
			{
				if (IsValidRecord(vResult) == true)
				{
					return true;
				}
				else
				{
					return false;
				}
			}
		}		
		
		test.TestCase(
			'RecordQuery',
			RecordQuery,
			{
				'Table': strTable,
				'Query': query
			},
			vExpected,
			TestCase_RecordQuery_CheckResult);
	}	
	
	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	
	var strTestItem1 = testdata.CreateTestItem('ITEM1','VALUE1');
	var strTestItem2 = testdata.CreateTestItem('ITEM2','VALUE2');
	
	TestCase_RecordQuery('x_snc_devtools_test',{ 'name': testdata.GetPrefix()+'ITEM1' },true);
	TestCase_RecordQuery('x_snc_devtools_test',{ 'name': testdata.GetPrefix()+'ITEM2' },true);
	TestCase_RecordQuery('x_snc_devtools_test',{ 'name': testdata.GetPrefix()+'ITEM_DOES_NOT_EXIST' },false);
	
	test.TestCase_RecordGetValue('x_snc_devtools_test',strTestItem1,'value','VALUE1');
	test.TestCase_RecordGetValue('x_snc_devtools_test',strTestItem2,'value','VALUE2');
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
