sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetVariablesFromRecord(grRecord, vExpected)
	{
		function TestCase_GetVariablesFromRecord_CheckResult(vInput,vExpected,vResult)
		{
			return IsEqual(vExpected,vResult);
		}	
		
		test.TestCase(
			'Function GetVariablesFromRecord()',
			GetVariablesFromRecord,
			{
				'Record': grRecord 
			},
			vExpected,
			TestCase_GetVariablesFromRecord_CheckResult);
	}


	TestCase_GetVariablesFromRecord(null,false);
	TestCase_GetVariablesFromRecord(new GlideRecord('incident'),{});

	var grItem = new GlideRecord('sc_req_item');
	grItem.initialize();
	var strItemSysId = grItem.insert();
	
	var grItemOptionNew = new GlideRecord('item_option_new');
	grItemOptionNew.initialize();
	grItemOptionNew.name = 'test'; 
	grItemOptionNew.type = 6;
	grItemOptionNew.question_text = 'What do you say?';
	var strItemOptionNewSysId = grItemOptionNew.insert();
	
	test.Log(RenderValue(grItemOptionNew));
		
	var grItemOption = new GlideRecord('sc_item_option');
	grItemOption.initialize();
	grItemOption.value = 'Hello World';
	grItemOption.item_option_new = strItemOptionNewSysId;
	var strItemOptionSysId = grItemOption.insert();
	
	test.Log(RenderValue(grItemOption));
	
	var grItemOptionMtom = new GlideRecord('sc_item_option_mtom');
	grItemOptionMtom.initialize();
	grItemOptionMtom.request_item = strItemSysId;
	grItemOptionMtom.sc_item_option = strItemOptionSysId;
	var strItemOptionMtomSysId = grItemOptionMtom.insert();	
	
	test.Log(RenderValue(grItemOptionMtom));
	
	grItem.get(strItemSysId);
	
	// TODO: Actually we expect the variable to show up, but something in the test data setup seems to be missing!
	TestCase_GetVariablesFromRecord(grItem,{});
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
