sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;
	

	function TestCase_PerformanceQuery(
		vInputName,
		vInputTable,
		vInputQuery,
		vInputOrderBy,
		vInputUser,
		vInputRepetitions,
		vInputLimit,
		vExpected)
	{
		Log('TestCase_PerformanceQuery');
		Log('Input Name       : '+RenderValue(vInputName));
		Log('Input Table      : '+RenderValue(vInputTable));
		Log('Input Query      : '+RenderValue(vInputQuery));
		Log('Input Order by   : '+RenderValue(vInputOrderBy));
		Log('Input User       : '+RenderValue(vInputUser));
		Log('Input Repetitions: '+RenderValue(vInputRepetitions));
		Log('Input Limit      : '+RenderValue(vInputLimit));
		Log('Expected         : '+RenderValue(vExpected));
		var vResult = PerformanceQuery(vInputName,vInputTable,vInputQuery,vInputOrderBy,vInputLimit,vInputUser,vInputRepetitions);
		Log('Result           : '+RenderValue(vResult));
		if (vExpected == false)
		{
			if (vResult != vExpected)
			{
				bResult = false;
				Log('Test case FAILED - false was expected');
			}
		}
		else
		{
			if (GetValue(vResult,'repetitions') != vInputRepetitions)
			{
				bResult = false;
				Log('Test case FAILED - repetitions did not met expectations');
			}
			if (vInputLimit > 0)
			{
				if (GetValue(vResult,'records') > vInputLimit)
				{
					bResult = false;
					Log('Test case FAILED - number of records does not comply with set limit');
				}
			}
		}
		Log('');
	}
	
	TestCase_PerformanceQuery('No results','','','','_ATF_DEVTOOLS_NOACCESS',2,0,false);	
	
	var testdata = new TestDataAPI('_ATF_DEVTOOLS_');
	testdata.CreateUser('NOACCESS',[]);
	testdata.CreateUser('ADMIN',['admin']);
	
	TestCase_PerformanceQuery('All incidents','incident','','','_ATF_DEVTOOLS_NOACCESS',2,0,true);
	TestCase_PerformanceQuery('All incidents','incident','','','_ATF_DEVTOOLS_ADMIN',2,0,true);
	TestCase_PerformanceQuery('Active incidents','incident','active=true','','_ATF_DEVTOOLS_NOACCESS',2,0,true);
	TestCase_PerformanceQuery('Active incidents','incident','active=true','','_ATF_DEVTOOLS_ADMIN',2,0,true);
	TestCase_PerformanceQuery('One sorted incident','incident','active=true','number','_ATF_DEVTOOLS_NOACCESS',2,1,true);
	TestCase_PerformanceQuery('One sorted incident','incident','active=true','number','_ATF_DEVTOOLS_ADMIN',2,1,true);

	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
