sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	var strItemSysId = '';

	function TestCase_RecordQueryOrInsert(strTable, query, values,bUpdate, vExpected)
	{
		test.TestCase(
			'Function RecordQueryOrInsert()',
			RecordQueryOrInsert,
			{
				'Table': strTable,
				'Query': query,
				'Values': values,
				'Update': bUpdate
			},
			vExpected,
			TestCase_RecordQueryOrInsert_CheckResult);
	}



	function TestCase_RecordQueryOrInsert_CheckResult(vInput,vExpected,vResult)
	{
		if (vExpected === true)
		{
			if (IsValidSysId(vResult))
			{
				strItemSysId = vResult;
				return true;
			}
			return false;
		}
		return (vExpected == vResult);
	}
	
	var testdata = new x_snc_devtools.TestDataAPI('_ATF_DEVTOOLS_');
	
	var strTable = 'x_snc_devtools_test';
	
	TestCase_RecordQueryOrInsert(
		strTable,
		{
			name: testdata.GetPrefix()+'ITEM_1'
		},
		{
			value: 'value1'
		},
		false,
		true
		);
	test.TestCase_RecordGetValue(strTable,strItemSysId,'value','value1');

	test.Log(RenderValue(testdata.GetAllTestItems())+'\n');

	TestCase_RecordQueryOrInsert(
		strTable,
		{
			name: testdata.GetPrefix()+'ITEM_1'
		},
		{
			value: 'value2'
		},
		false,
		true
		);
	test.TestCase_RecordGetValue(strTable,strItemSysId,'value','value1');

	
	test.Log(RenderValue(testdata.GetAllTestItems())+'\n');

	TestCase_RecordQueryOrInsert(
		strTable,
		{
			name: testdata.GetPrefix()+'ITEM_1'
		},
		{
			value: 'value2'
		},
		true,
		true
		);
	test.TestCase_RecordGetValue(strTable,strItemSysId,'value','value2');
	
	test.Log(RenderValue(testdata.GetAllTestItems())+'\n');
	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
