sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RecordAttachmentAdd(strTable, strRecordSysId, strFileName, strContentType, strContent, vExpected)
	{
		function TestCase_RecordAttachmentAdd_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == vResult)
			{
				return true;
			}
			if (vExpected == true)
			{
				return IsValidSysId(vResult);
			}
			return false;
		}
		test.TestCase(
			'Function RecordAttachmentAdd()',
			RecordAttachmentAdd,
			{
				'Table': strTable,
				'Record': strRecordSysId,
				'Filename': strFileName,
				'Content Type': strContentType,
				'Content': strContent
			},
			vExpected,
			TestCase_RecordAttachmentAdd_CheckResult);
	}


	TestCase_RecordAttachmentAdd(null,null,'test.txt','text/plain','This is a test!',false);
	
	var testdata = new TestDataAPI('_DEVTOOLS_');
	var strRecordSysId = testdata.CreateTestItem('ATTACHMENT1');
	
	TestCase_RecordAttachmentAdd('x_snc_devtools_test',strRecordSysId,'test.txt','text/plain','This is a test',true);
	
	var gsa = new GlideSysAttachment();
	var grAttachments = gsa.getAttachments('x_snc_devtools_test', strRecordSysId);

	while (grAttachments.next())
	{
		test.Log(RenderRecordSummary(grAttachments));
	}
	if (grAttachments.getRowCount() != 1)
	{
		test.Log('FAILED: Record should have exactly one attachment!');
		test.SetResult(false);
	}
	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
