sys_script_include.script:
function IsValidUrl(strUrl)
{

	function GetRegExURL()
	{
		var strIPregex = '\\b(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\\b';

		var strURLregex =
			'((https?|ftp)\\:\\/\\/)?' // SCHEME 
			+ '([a-z0-9+!*(),;?&=$_.-]+(\\:[a-z0-9+!*(),;?&=$_.-]+)?@)?' // USER and PASSWORD
			+ '(([a-z0-9-.]*)\\.([a-z]{2,20})|'+strIPregex+'|localhost)' // HOST with TLD or IP or "localhost"
			+ '(\\:[0-9]{2,5})?' // PORT
			+ '(\\/([a-z0-9!$%\\/()=@+~,:\\-_]\\.?)+)*\\/?' // PATH allowing double slashes and commas, equal signs...
			+ '(\\?[a-z\\:+&$_.-][a-z0-9;:@&%=+\\/$_.\\?\\{\\}\\-]*)?' // GET Query      
			+ '(#([a-z!_\\.\\-\\/][a-z0-9:&%!+\\/=$_\\.\\?\\-]*)?)?' // ANCHOR 
			;

		strURLregex = '^'+strURLregex+'$';

		return strURLregex;
	}

	strUrl = GetStringValue(strUrl);

	if (strUrl == '')	
	{
		return false;
	}
	/*
	if (StringCheckCharSet($strURL,CHARSET_URL) == false)
	{
		return false;	
	}
	*/
	if (
		(strUrl.substr(0,7) != "http://")
		&& 	(strUrl.substr(0,8) != "https://")
		&& 	(strUrl.substr(0,6) != "ftp://") 
	)
	{
		return false;
	}

	strUrl = strUrl.toLowerCase(); 
	if (StringCheckRegEx(strUrl,GetRegExURL()) == false) 
	{ 
		return false; 
	} 

	return true;

}
