sys_script_include.script:
function GetAppUncommittedFiles(vApp)
{
	var grApp = GetAppRecord(vApp);
	if (grApp == false)
	{
		return false;
	}
	if (GetAppCurrentBranch(grApp) == '')
	{
		return false;
	}
	var aUncommittedFiles = [];

	var grUpdateXml = new GlideRecord('sys_update_xml');
	grUpdateXml.addQuery('application','=',grApp.sys_id);
	grUpdateXml.addQuery('remote_update_set','=','');
	grUpdateXml.query();
	while (grUpdateXml.next())
	{
		var payload = ParseXml(grUpdateXml.payload);
		if (payload != false)
		{
			var strClassName = GetValue(payload,'record_update','table');
			var strSysId = GetValue(payload,'record_update',strClassName,'sys_id');
			var grRecord = GetRecord(strClassName,strSysId);
			if (IsValidRecord(grRecord) == true)
			{
				var uncommittedFile = Clone(grRecord);
				aUncommittedFiles.push(uncommittedFile);
			}
		}
	}
	return aUncommittedFiles;
}
