sys_script_include.script:
function HtmlRenderError(strText)
{
	strText = GetStringValue(strText);
	var strHtml = '';
	//strHtml += '<div id="output_messages" class="outputmsg_container ">';
	strHtml += '<div class="outputmsg outputmsg_error notification notification-danger">';
		strHtml += '<div class="outputmsg_text">';
			strHtml += strText;
		strHtml += '</div>';
	strHtml += '</div>';
	return strHtml;
}
