sys_script_include.script:
function GetTestRecord(grRecord)
{
	if (IsValidRecord(grRecord) == false)
	{
		return false;
	}
	if (grRecord.getRecordClassName() == 'sys_script_include')
	{
		var grTest = new GlideRecord('sys_atf_test');
		grTest.addQuery('name','ENDSWITH',grRecord.name);
		grTest.addQuery('sys_scope','=',grRecord.sys_scope);
		grTest.query();
		if (grTest.next() == true)
		{
			return grTest;
		}		
	}
	return false;
}
