sys_ui_script.script:
function FormatValue(vValue,config)
{
	var strValue = ''+vValue;

	if (strValue == '')
	{
		return '';
	}

	var strType = 'string';

	var nDecimals = GetValue(config,'decimals');
	if (nDecimals != null)
	{
		strType = 'number';
		nDecimals = parseInt(nDecimals);
	}

	if (strType == 'number')
	{
		strValue = strValue.replaceAll(',','');
		var fValue = parseFloat(strValue);
		strValue = fValue.toFixed(nDecimals);
	}

	var strPostFix = GetValue(config,'postfix');
	if (strPostFix == undefined)
	{
		strPostFix = '';
	}
	strValue += strPostFix;
	return strValue;
}
