sys_ui_script.script:
function GetCurrentRecordSysId()
{
	if (IsForm())
	{
		return g_form.getUniqueValue();
	}
	if (IsList())
	{
		if (typeof rowSysId != 'undefined')
		{
			return rowSysId;
		}

		var element = document.getElementById('sys_uniqueValue');
		if (element != null)
		{
			return element.value;
		}
	}
	return '';
}
