sys_ui_script.script:
function GetBoolValue(vInput)
{
	if (vInput == undefined)
	{
		return false;
	}
	if (vInput == null)
	{
		return false;
	}
	if (vInput === true)
	{
		return true;
	}
	if (vInput == 'true')
	{
		return true;
	}
	if (vInput == 'false')
	{
		return false;
	}
	if (vInput == 'null')
	{
		return false;
	}
	if (vInput != 0)
	{
		return true;
	}
	return false;
}
